import React from 'react';
import { Text, StyleSheet, View, Button, ImageBackground } from "react-native";

export default function Detalhes({route, navigation}){

    const dados = route.params;
    return (

        <ImageBackground source={require('../../img/fundo2.jpg')} resizeMode="cover" style={{height:800, width:490}}>
            <View style={{flex:1, alignItems:'center', justifyContent:'center' }}>
                 <View>
                    <Text>Detalhes</Text>
                    <Text>{`\nc√≥digo: ${dados.id} \nproduto: ${dados.nome} \nR$ ${dados.preco}`}</Text>
                    <Button title="Ir para Detalhes"
                        onPress={() => navigation.push('Detalhes', dados)}/>

                    <Button title="Tela anterior"
                        onPress={() => navigation.goBack()}/>

                    <Button title="Retornar a Home"
                        onPress={() => navigation.popToTop()}/>
                </View>        
            </View>
        </ImageBackground>
    )
}
