import React from 'react';
import { Text, StyleSheet, View, Button, ImageBackground } from "react-native";

export default function Detalhes({route, navigation}){

    const dados = route.params;
    return (

        <ImageBackground source={require('../../img/fundo3.jpg')} resizeMode="cover" style={{height:800, width:490}}>
            <View style={{flex:1, alignItems:'center', justifyContent:'center' }}>
                 <View style={{marginRight:80, marginBottom:300}}>
                    <Text>Detalhes</Text>
                   
                 
                     <Button title="Ir para Detalhes"
                        onPress={() => navigation.navigate('Detalhes')}/>
               

          
                </View>        
            </View>
        </ImageBackground>
    )
}



