import React, { useState } from 'react';
import { Text, StyleSheet,View, Button, TextInput, Image, TouchableOpacity} from "react-native";
import { styles } from '../Style/style';
import Dados from './Dados';



export default function Form({navigation}){

    const [dados, setDados] = useState({
        valor:0,
        acr:0.70,
        satual:1.000
    
    });

    const [resultado, setResultado] = useState(0);

    return (
        <View style={styles.dados}>

            <View>
                <Image style={styles.formImg} source={require('../../img/Logo1.png')}/>
            </View>
           
            <Text style={styles.dadosText}>Dados para Depósito</Text>

            <View style={styles.form}>

                {/* <Text style={styles.formText1}>{`Agência: ${dados.agencia}`}</Text> */}
                <Text style={styles.dadosText1}>{`\nAgência Bancária: ${dados.agencia}`}</Text>
            </View>

            
            <View style={styles.form}>

                <Text style={styles.formText1}>{`Conta Corrente:${dados.contaCorrente}`}</Text>

                {/* <TextInput style={styles.formInput}  placeholder='Digite sua Conta'  keyboardType="numeric"/> */}

            </View>
            
            <View style={styles.form}>

                <Text style={styles.formText1}>Valor do Depósito: R$</Text>

                
                <TextInput placeholder="Digite o valor" style={styles.formInput} 
            onChangeText={
                (valor) => setDados ({
                    ...dados, valor:  parseInt(valor)
                })
            }/>

            </View>

            <View>
                
                <Text style={styles.formText1}>{`Seu valor do Depósito:`}</Text>     
                <Text style={styles.formText2}>{`       R$ ${resultado + dados.valor}`}</Text> 

                <TouchableOpacity style={styles.formTouch} onPress={() => setResultado(dados.valor * dados.acr)}>
                <View><Text style={styles.formTouchText}>Depositar</Text></View></TouchableOpacity> 

                <Text style={styles.formText1}>{`Saldo Atual:`}</Text>     
                <Text style={styles.formText2}>{`       R$ ${resultado + dados.satual}`}</Text> 
            </View>

         

        
            
        </View>
    )
}
